import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [question, setQuestion] = useState("");
  const [btnPressed, setBtnpress] = useState(false);
  const [answers, setAnswers] = useState([]);
  const [lives, setLives] = useState(3);
  const [score, setScore] = useState(0);

  useEffect(() => {
    fetch(`https://opentdb.com/api.php?amount=3&difficulty=easy&encode=url3986`)
      .then((res) => res.json())
      .then((data) => setQuestion(data.results[0]));
  }, []);

  useEffect(() => {
    const unshuffledAnswers = [
      question.correct_answer,
      question.incorrect_answers,
    ];
    const flattenedAnswers = unshuffledAnswers.flat();
    const shuffled = flattenedAnswers.sort(() => Math.random() - 0.5);
    setAnswers(shuffled);
  }, [question]);

  function onClickHandler() {
    setBtnpress(true);
  }

  return (
    <div className="App">
      {btnPressed ? (
        <div>
          <div>
            <p>Lives:{lives}</p>
            <p>/</p>
            <p>Score:{score}</p>
          </div>
          <p>{question.question}</p>
          {answers.map((e) => {
            return <button onClick={onClickHandler1}>{e}</button>;
          })}
        </div>
      ) : (
        <button onClick={onClickHandler}> Start!</button>
      )}
    </div>
  );
}

export default App;

