import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  function QuestionFetcher(props) {
    const [question, setQuestion] = useState("");

    useEffect(() => {
      fetch(`https://opentdb.com/api.php?amount=3&difficulty=easy&encode=url3986`)
        .then((res) => res.json())
        .then((data) => console.log(setQuestion(data.result[3])));
    }, [props]);

    if (!question) {
      return null;
