import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [question, setQuestion] = useState("");
  const [btnPressed, setBtnpress] = useState(false);
  const [lives, setLives] = useState(3);
  const [score, setScore] = useState(3);

  useEffect(() => {
    fetch(`https://opentdb.com/api.php?amount=3&difficulty=easy&encode=url3986`)
      .then((res) => res.json())
      .then((data) => setQuestion(data.results[0].question));
  }, []);
  //console.log(question);

  function onClickHandler() {
    setBtnpress(true);
  }

  return (
    <div className="App">
      {btnPressed ? (
        <div>
          <div>
      <p>Lives:{lives}</p>
            <p>/</p>
            <p>Score:</p>
          </div>
          <p>{question}</p>
        </div>
      ) : (
        <button onClick={onClickHandler}> Start!</button>
      )}
    </div>
  );
}

export default App;

