import React, { useState, useEffect } from "react";
import "./App.css";

function QuestionFetcher() {
  const [question, setQuestion] = useState("");

  useEffect(() => {
    fetch(`https://opentdb.com/api.php?amount=3&difficulty=easy&encode=url3986`)
      .then((res) => res.json())
      .then((data) => console.log(setQuestion(data.result[3])));
  }, [props]);

  if (!btnPressed) {
    return null;
  } else {
    return setBtnpress(data.question);
  }
}

function App() {
  const [btnPressed, setBtnpress] = useState(0);

  function onClickHandler() {
    setBtnpress(btnPressed + 1);
  }

  return (
    <div className="App">
      <p>{question.question}</p>
      <button onClick={onClickHandler}> Start!</button>
    </div>
  );
}

export default App;
