import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [question, setQuestion] = useState("");
  const [btnPressed, setBtnpress] = useState(0);

  useEffect(() => {
    fetch(`https://opentdb.com/api.php?amount=3&difficulty=easy&encode=url3986`)
      .then((res) => res.json())
      .then((data) => setQuestion(data.result[3]));
  }, []);
  console.log(question);

  // if (!question) {
  //   return null;
  // } else {
  //   return setQuestion(data.result);
  // }

  function onClickHandler() {
    setBtnpress(btnPressed + 1);
  }

  return (
    <div className="App">
      <p>{question.data}</p>
      <button onClick={onClickHandler}> Start!</button>
    </div>
  );
}

export default App;

