import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [question, setQuestion] = useState("");
  const [btnPressed, setBtnpress] = useState(0);

  function onClickHandler() {
    setBtnpress(btnPressed + 1);
  }

  useEffect(() => {
    if (btnPressed >= 1) {
      fetch(
        `https://opentdb.com/api.php?amount=3&difficulty=easy&encode=url3986`
      )
        .then((res) => res.json())
        .then((data) => console.log(setQuestion(data.result[3])));
    }
  }, [btnPressed]);

  return (

    <div className="App">
      <p>{question}</p>
      <button onClick={onClickHandler}> Start!</button>
    </div>
  );
}

export default App;
